import React, { Component } from 'react';
import Markets from './index'
class MarketsContainer extends Component{

  constructor(props) {
    super();
      this.state = {
        symbol: [],
        isLoaded: false,
        //isLoaded: false,
      };

}

   _getTickerBySymbol(data) {
         let ticker = {}
         data.forEach(item => {
             let symbol = item.symbol || item.s;
             ticker[symbol] = {
                 symbol: symbol,
                 lastPrice: item.lastPrice || item.c,
                 priceChange: item.priceChange || item.p,
                 priceChangePercent: item.priceChangePercent || item.P,
                 highPrice: item.highPrice || item.h,
                 lowPrice: item.lowPrice || item.l,
                 quoteVolume: item.quoteVolume || item.q,
             }
         })
         return ticker;

     }




componentDidMount(){

  //return fetch('http://192.168.0.100:4440/24h')

  return fetch('https://api.binance.com/api/v1/ticker/24hr')

              .then((response) => response.json())
              .then((responseJson) => {
                console.log(responseJson)
                this.setState({
                  ticker: responseJson.data
                })
              })

              //.then((res) => res.json())
              //.then((respo) => {
              //  console.log(respo)
              //  let ticker = this._getTickerBySymbol((respo.data).data)
              //  return ticker;
              //}) 


              .catch((error) => {
                if(error === 'TypeError: Network request failed'){
                  console.log('error internet')
                }
                console.error(error);
              });

}


  render(){

    //var { respo , ticker  } = this.state;

   // if(!isLoaded){
   //   return <div>Loading...</div>
   // }

        return(
          <div>
            <ul>
              {this.state.symbol.map(symbol => (
                <li key= {symbol}> symbol </li>

              ))} 
            </ul>

          </div>
        )

  }
}

export default MarketsContainer;
