   // if(!isLoaded){
   //   return <div>Loading...</div>
   // }
   var symbolo = this.state;

    return(

          <div>

            <Table responsive>
              <thead>
                <tr>
                  <th>Pair</th>
                  <th>Last Price</th>
                  <th>24h Change</th>
                  <th>24h High</th>
                  <th>24h Low</th>
                  <th>24h Volume</th>
                </tr>
              </thead>
              <tbody>
                {this.state.data.map(item => (

                   <tr key={item.symbol}>

                     <td>  <div>  </div>
                     {symbolo}</td>
                     <td>{item.lastPrice}</td>
                     <td>{item.priceChangePercent}%</td>
                     <td>{item.highPrice}</td>
                     <td>{item.lowPrice}</td>
                     <td>{item.quoteVolume}</td>
                   </tr>

                ))}

              </tbody>
            </Table>
        </div>

    )

  }
}

export default MarketsContainer;
