import React, { Component } from 'react';
import TableMarket  from './index'

class MarketsContainer extends Component{

  constructor(props) {
    super();
      this.state = {
        data: [],
      };  

}



componentDidMount(){

  //return fetch('http://192.168.0.100:4440/24h')
  return fetch('https://api.binance.com/api/v1/ticker/24hr')

              .then((response) => response.json())
              .then((responseJson) => {
                console.log(responseJson)      
                 this.setState({
                  data: responseJson
                })
              })

              .catch((error) => {
                if(error === 'TypeError: Network request failed'){
                  console.log('error internet')
                }
                console.error(error);
              });      
}


  render(){
   //var {  datas  } = this.state;
   // if(!isLoaded){
   //   return <div>Loading...</div>
   // }
    return(


      <div>
        <TableMarket  />
          <ul>
            {this.state.data.map(item => (
              <li key={item.symbol}>{item.symbol}</li>

            ))}
          </ul>
          <ul>
               <li>{item.lastPrice}</li> 
          </ul>
      </div>

    )

  }
}

export default MarketsContainer;
