import React, { Component } from 'react';
import axios from 'axios'


class MarketsContainer extends Component{

  constructor(props) {
    super();
      this.state = {
        response: {}
      };

}

  _getTickerBySymbol(data) {
        let ticker = {}
        data.forEach(item => {
            let symbol = item.symbol || item.s;
            ticker[symbol] = {
                symbol: symbol,
                lastPrice: item.lastPrice || item.c,
                priceChange: item.priceChange || item.p,
                priceChangePercent: item.priceChangePercent || item.P,
                highPrice: item.highPrice || item.h,
                lowPrice: item.lowPrice || item.l,
                quoteVolume: item.quoteVolume || item.q,
            }
        })
        return ticker;

    }


componentDidMount(){

  axios.get('https://api.binance.com/api/v3/avgPrice' )

        .then((response) => response.json())

        .then(res => {
          let ticker = this._getTickerBySymbol(JSON.parse(res.data).data)
          console.log(ticker);

          this.props.dispatch({
            type: 'UPDATE_MARKET_PAIRS',
            data: ticker


          })


        });

}


  render(){
    return(
      <div>

      </div>
    )
  }
}

export default MarketsContainer;
